			, @context
			, error_number()
			, error_severity()
			, error_state()
			, error_procedure()
			, error_line()
			, error_message()
			, db_name()
			, @@spid
			, @@servername
			, '''' -- HostName
			, '''' -- ProgramName
			, '''' -- NTDomain
			, '''' -- NTUsername
			, '''' -- LoginName
			, '''' -- OriginalLoginName
			, null
	end catch

	set nocount off
end
');

