		--!
		--!
		--!
		set @_Step = ''Populate SSIS_STG_BUDGET_2017_Sales''

		; with dateKeyCte
		as
		(
			select
				  min(DateKey) as [DateKey]
				, FiscalPeriodCode
			from
				dbo.Calendar
			where
