			, try_cast(replace(src.IC_Transf_Region_11100, '','', '''') as decimal(38,16)) as [IC_Transf_Region_11100]
			, try_cast(replace(src.IC_Transf_Abroad_11200, '','', '''') as decimal(38,16)) as [IC_Transf_Abroad_11200]
