					, ''N/A''	-- EnglishMonthName
					, ''N/A''	-- EnglishDayCode
					, ''N/A''	-- EnglishDayName
					, ''N/A''	-- YearMonth
					, ''N/A''	-- YearQuarter
					, ''N/A''	-- IsWeekend
					, ''N/A''	-- IsLastDayInMonth
